# struct
import struct
import base64

n = 10240099
b1 = (n & 0xff000000) >> 24
b2 = (n & 0xff0000) >> 16
b3 = (n & 0xff00) >> 8
b4 = n & 0xff
bs = bytes([b1, b2, b3, b4])
print(bs)


print(struct.pack('>I', 10240099))
print(struct.unpack('>IH', b'\xf0\xf0\xf0\xf0\x80\x80'))


# 课后习题
bmp_data = base64.b64decode('')


def bmp_info(data):
    info = struct.unpack('<ccIIIIIIHH', bmp_data[:30])
    if info[0] + info[1] == b'BM':
        return {
            'width': info[6],
            'height': info[7],
            'color': info[9]
        }


# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')
